mapper.select:
SELECT good.id, good.good_name, good.description, good.promotion, good.good_no, good.price, good.seo, good.main_image_url, tcateg.category_id as category_id FROM t_good good LEFT JOIN t_category_good tcateg ON tcateg.good_id=good.id LEFT JOIN t_category tcat ON tcateg.category_id=tcat.id WHERE good.on_sale='ON_SALE' AND good.status='NORMAL' AND good.audit='PASS_AUDIT' AND good.seo LIKE #{searchKey}
AND(tcat.id=#{categoryId} OR tcat.parent_id=#{categoryId})
GROUP BY good.id
